import { createReducer, on } from '@ngrx/store';

export interface State {}

export const initialState: State = {};

export const REDUCER_NAME = createReducer(initialState);

import { createAction, props } from '@ngrx/store';

export const ActionTypes = {
  NEW_ACTION: '[Type] ACTION',
};

export const someAction = createAction(
  ActionTypes.NEW_ACTION,
  props<{ propOne: string; propTwo: string }>()
);

import { createSelector, createFeatureSelector } from '@ngrx/store';
import { State } from './SOME_REDUCER.reducer';

export const selectState = createFeatureSelector<State>('NAME OF STATE');

export const selectSomeState = createSelector(
  selectState,
  (state: State) => state.someProp
);
